var expect=chai.expect;describe("Clase Medida",function(){describe("constructor medida",function(){it("deberia tener un constructor",function(){var e=new Medida(32,"f");expect(e.valor).to.equal(32)}),it("deberia poder recibir un solo parametro",function(){var e=new Medida("32e-1 f");expect(e.valor).to.equal(3.2),expect(e.tipo).to.equal("f")})}),describe("funcion match",function(){it("deber√≠a cazar bien",function(){var e=Medida.match("330e-1 f to c");expect(parseFloat(e.val)).to.equal(33),expect(e.tip).to.equal("f"),expect(e.para).to.equal("c")})}),describe("funcion convertir",function(){it("deberia convertir correctamente",function(){var e=Medida.convertir("320e-1 f to c");expect(e).to.equal("0.00 Celsius")})})}),describe("Clase Temperatura",function(){describe("constructor temperatura",function(){it("deberia tener un constructor",function(){var e=new Temperatura(32,"c");expect(e.valor).to.equal(32)}),it("deberia poder recibir un solo parametro",function(){var e=new Temperatura("32 c");expect(e.valor).to.equal(32),expect(e.tipo).to.equal("c")})})}),describe("Clase Fahrenheit",function(){describe("constructor fahrenheit",function(){it("deberia tener un constructor",function(){var e=new Fahrenheit(32);expect(e.valor).to.equal(32)})}),describe("funciones de fahrenheit",function(){it("debe convertir a celsius",function(){var e=new Fahrenheit(32);expect(e.toCelsius()).to.equal(0)}),it("debe convertir a kelvin",function(){var e=new Fahrenheit(32);expect(e.toKelvin()).to.equal(273.15)})})}),describe("Clase Celsius",function(){describe("constructor celsius",function(){it("deberia tener un constructor",function(){var e=new Celsius(32);expect(e.valor).to.equal(32)})}),describe("funciones de celsius",function(){it("debe convertir a fahrenheit",function(){var e=new Celsius(32);expect(e.toFahrenheit()).to.equal(89.6)}),it("debe convertir a kelvin",function(){var e=new Celsius(32);expect(e.toKelvin()).to.equal(305.15)})})}),describe("Clase Kelvin",function(){describe("constructor kelvin",function(){it("deberia tener un constructor",function(){var e=new Kelvin(88);expect(e.valor).to.equal(88)})}),describe("funciones de kelvin",function(){it("debe convertir a celsius",function(){var e=new Kelvin(88);expect(parseFloat(e.toCelsius().toFixed(2))).to.equal(-185.15)}),it("debe convertir a fahrenheit",function(){var e=new Kelvin(88);expect(parseFloat(e.toFahrenheit().toFixed(2))).to.equal(-301.27)})})});